!function() {
    tinymce.PluginManager.add("BL_MTTip", function(e, t) {
        e.addButton("BL_MTTip_button", {
            title:"ModernToolTip", icon:"mce", onclick:function() {
                e.windowManager.open( {
                    title:"Insert a BlueLevel ModernToolTip ToolTip", body:[ {
                        type: "textbox", name: "title", label: "ModernToolTip Title"
                    }
                    , {
                        type: "textbox", multiline: true, width: 500, height: 50, name: "cont", label: "ModernToolTip Content"
                    }
                    , {
                        type:"listbox", name:"effect", label:"ModernToolTip Effect", values:[ {
                            text: "StandardEffect 1", value: "tooltip-effect-1"
                        }
                        , {
                            text: "StandardEffect 2", value: "tooltip-effect-2"
                        }
                        , {
                            text: "StandardEffect 3", value: "tooltip-effect-3"
                        }
                        , {
                            text: "StandardEffect 4", value: "tooltip-effect-4"
                        }
                        , {
                            text: "StandardEffect 5", value: "tooltip-effect-5"
                        }
                        ]
                    }
                    ], onsubmit:function(t) {
                        e.insertContent('[BL_MToolTip effect="'+t.data.effect+'" title="'+t.data.title+'" content="'+t.data.cont+'" ]')
                    }
                }
                )
            }
        }
        )
    }
    )
}

();